import {socket} from './socket.js'
import {Board} from './classes/Board.js'
import {Pawn} from './classes/pieces/Pawn.js'
import {Rook} from './classes/pieces/Rook.js'
import {Knight} from './classes/pieces/Knight.js'
import {Bishop} from './classes/pieces/Bishop.js'
import {Queen} from './classes/pieces/Queen.js'
import {King} from './classes/pieces/King.js'
import {Color} from './types/Color.js';
/*** VARIABLES CONSTANTES ***/

    /**
    * @type {integer} WIDTH_CANVAS = Anchura del canvas
    * @type {integer} HEIGHT_CANVAS = Altura del canvas
    */
    const WIDTH_CANVAS = 800;
    const HEIGHT_CANVAS = 700;

    /**
    * @type {integer} FILES = Filas del TABLERO
    * @type {integer} RANKS = Columnas del TABLERO
    */
    const FILES = 8;
    const RANKS = 8;

/*** --VARIABLES CONSTANTES-- ***/

/*** THEMES ***/

    /**
    * @type {Object} theme = Themes para nuestro tablero
    * @type {{light: string, dark: string}}
    */
    const theme = {
        light: '#eeeed2',
        dark: '#769656'
    }

    /**
    * @type {Object} pieceTheme = Themes para piezas Ajedrez
    * @type {{light: string, dark: string}}
    */
    const pieceTheme = {
        light: '#FFFFFF',
        dark: '#000000'
    }

/*** --THEMES-- ***/

/*** Inicializacion de la clase Board ***/
const board = new Board(WIDTH_CANVAS, HEIGHT_CANVAS, FILES, RANKS, theme, pieceTheme);
/*** --Inicializacion de la clase Board ***/

/*** FUNCIONES ***/

    /**
    * @function printPawns = Pinta los peones del tablero 
    */
   function printPawns(){
        for (let i = 0; i < RANKS; i++){
            board.initPlacePiece(i, 1, new Pawn(Color.THEME.dark));

            board.initPlacePiece(i, 6, new Pawn(Color.THEME.light));
        }
   }

    /**
    * @function printPieces = Pinta las piezas del tablero 
    */
    function printPieces(){
        for (let i = 0; i < 2; i++){
            /* Si 'i' es mayor que 0 entonces pintamos las piezas de color Blanco */
            board.initPlacePiece(0, i * 7, new Rook(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(7, i * 7, new Rook(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(2, i * 7, new Bishop(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(5, i * 7, new Bishop(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(1, i * 7, new Knight(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(6, i * 7, new Knight(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(3, i * 7, new Queen(i > 0 ? Color.THEME.light : Color.THEME.dark));
            board.initPlacePiece(4, i * 7, new King(i > 0 ? Color.THEME.light : Color.THEME.dark));
        }
    }

/*** --FUNCIONES-- ***/

/* Pintamos las piezas */
printPawns();
printPieces();

/* Renderizamos el tablero */
board.renderBoard();
